import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'VPN Management System';
  apiOnline = false;
  systemHealthy = false;
  clientCount = 0;
  lastResult = '';

  private readonly API_BASE = 'http://localhost:8080';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.testAPI();
  }

  async testAPI(): Promise<void> {
    try {
      const response = await fetch(`${this.API_BASE}/health`);
      if (response.ok) {
        this.apiOnline = true;
        this.lastResult = '✅ API is online and responding';
        this.loadClients();
      } else {
        throw new Error(`HTTP ${response.status}`);
      }
    } catch (error) {
      this.apiOnline = false;
      this.lastResult = `❌ API connection failed: ${error}`;
    }
  }

  async loadClients(): Promise<void> {
    try {
      const response = await fetch(`${this.API_BASE}/api/clients`);
      if (response.ok) {
        const clients = await response.json();
        this.clientCount = clients.length;
        this.systemHealthy = true;
        this.lastResult = `✅ Loaded ${clients.length} clients successfully`;
      } else {
        throw new Error(`HTTP ${response.status}`);
      }
    } catch (error) {
      this.systemHealthy = false;
      this.lastResult = `❌ Failed to load clients: ${error}`;
    }
  }

  async refreshStatus(): Promise<void> {
    try {
      const response = await fetch(`${this.API_BASE}/api/status`);
      if (response.ok) {
        const status = await response.json();
        this.systemHealthy = true;
        this.lastResult = `✅ System status: ${JSON.stringify(status, null, 2)}`;
      } else {
        throw new Error(`HTTP ${response.status}`);
      }
    } catch (error) {
      this.systemHealthy = false;
      this.lastResult = `❌ Failed to get status: ${error}`;
    }
  }
}
